html:
 {%for i in doc.items%}
 {%if i.serial_no%}
 {% set list1 = i.serial_no.split("\n") %}
 {%endif%}
    {% for item in list1 %}

<div style="text-align:center;">
    <b style="text-align:center;">{{i.item_name}}</b><br>
      <b style="text-align:center;">{{i.item_barcode}}</b><br>
    <b>Serial No.:{{item}}</b><br>


    {%set a=frappe.get_doc("Item",i.item_code)%}

    <b>Expiry Date: {{a.get_formatted("end_of_life")}}</b>

</div>
